import React from "react";

function Checkbox({ childCheckboxData, handleIndividualCheckboxChange }) {
  return (
    <div>
      {parentData.children.map((childCheckboxData, childIndex) => {
        return (
          <Checkbox
            key={childIndex}
            childCheckboxData={childCheckboxData}
            handleIndividualCheckboxChange={() =>
              handleIndividualCheckboxChange(parentIndex, childIndex)
            }
          />
        );
      })}
      <input
        type="checkbox"
        checked={childCheckboxData.isChecked}
        onChange={handleIndividualCheckboxChange}
      />
    </div>
  );
}

export default Checkbox;
