import "./styles.css";
import React from "react";
import { useCallback } from "react";
import { mockCheckboxData } from "./mockData/constants";

export default function App() {
  const [isclassCheckboxChecked, setisClassCheckbox] = React.useState(false);
  const [checkboxes, setCheckboxes] = React.useState(mockCheckboxData);
  const handleParentChange = (parentIndex) => {
    const copy = [...checkboxes];

    copy[parentIndex].isParentChecked = !copy[parentIndex].isParentChecked;
    copy[parentIndex].children = copy[parentIndex].children.map((ele) => {
      return {
        ...ele,
        isChecked: !ele.isChecked,
      };
    });

    setCheckboxes([...copy]);
    handleClassChange();
  };

  const handleIndividualChange = (e, id, ind) => {
    const copy = [...checkboxes];
    const index = checkboxes.findIndex((ele) => ele.id === id);
    const output = copy.find((ele) => ele.id === id);

    output.children = output.children.map((child, index) => {
      if (index === ind) {
        return {
          ...child,
          isChecked: !child.isChecked,
        };
      } else {
        return child;
      }
    });

    if (output.children.every((nn) => nn.isChecked)) {
      output.isParentChecked = true;
    } else {
      output.isParentChecked = false;
    }
    copy[index] = output;

    setCheckboxes([...copy]);
    handleClassChange();
  };
  const handleClass = () => {
    setClassCheckbox((prevData) => !prevData);
    handleAllCheckbox(!classCheckbox);
  };

  const handleAllCheckbox = useCallback(
    (classStatus) => {
      let copy = [...checkboxes];
      copy = copy.map((elem) => {
        const child = elem.children.map((ele) => ({
          ...ele,
          isChecked: classStatus,
        }));

        return {
          ...elem,
          isParentChecked: classStatus,
          children: child,
        };
      });

      setCheckboxes([...copy]);
    },
    [classCheckbox]
  );

  const handleClassChange = () => {
    if (checkboxes.every((ele) => ele.isParentChecked)) {
      setClassCheckbox(true);
    } else {
      setClassCheckbox(false);
    }
  };
  return (
    <div className="App">
      <div style={{ display: "flex" }}>
        <h3>Class 9</h3>
        <input
          type="checkbox"
          checked={classCheckbox}
          onChange={() => handleClass()}
        />
      </div>

      {checkboxes.map((data, parentIndex) => {
        return (
          <div>
            <div style={{ display: "flex" }}>
              <input
                type="checkbox"
                checked={data.isParentChecked}
                onChange={() => handleParentChange(parentIndex)}
              />
              <h2>{data.name}</h2>
            </div>

            <div>
              {data.children.map((ele, ind) => {
                return (
                  <div>
                    <input
                      type="checkbox"
                      checked={ele.isChecked}
                      onChange={(event) =>
                        handleIndividualChange(event, data.id, ind)
                      }
                    />
                  </div>
                );
              })}
            </div>
          </div>
        );
      })}
    </div>
  );
}
